<div class="container">
    <div class="row">
        <div class="col-sm-6">
            <div class="page-title">
                <h2  onClick={()=>this.handleBack()} >BACK</h2>
            </div>
        </div>
        <div class="col-sm-6">
            <nav aria-label="breadcrumb" class="theme-breadcrumb">
                <ol class="breadcrumb">
                    <li class="breadcrumb-item active" aria-current="page">Edit Address</li>
                </ol>
            </nav>
        </div>
    </div> 
</div>
</div>


<section class="contact-page register-page section-b-space my-4">
        <div class="container">
            <div class="row">
                <div class="col-sm-12">
                    <h3>SHIPPING ADDRESS</h3>
                    <form class="theme-form">
                        <div class="form-row">
                            <div class="col-md-6">
                                <label for="name">Full Name</label>
                                <input onChange={this.onChange} type="email" id="name" value={this.state.UserFullName} class="form-control" placeholder="Full Name"
                                    required=""/>
                            </div>
                            <div class="col-md-6">
                                <label for="name">Address *</label>
                                <input onChange={this.onChange} type="address" id="address" value={this.state.UserAddress} class="form-control"  placeholder="Address"
                                    required=""/>
                            </div>
                            <div class="col-md-6">
                                <label for="email">Zip Code *</label>
                                <input onChange={this.onChange} type="zip" id="zip"  class="form-control" value={this.state.UserZip}  placeholder="zip-code"
                                    required=""/>
                            </div>

                            <div class="col-md-6">
                                <label for="review">City *</label>
                                <input onChange={this.onChange} type="city" id="city" class="form-control" value={this.state.UserCity} placeholder="City" required=""/>
