      var msg = '** Enter Zip';
      toast.error(msg)

    }

    else if(UserCity===''){
      this.setState({isloading:false,})

      var msg = '** Enter City';
      toast.error(msg)

    }

    else if(!UserPhone.match(phoneno)){
      this.setState({isloading:false,})

      var msg = '** Enter Vaild Phone Number';
      toast.error(msg)

    }



    else{



    this.setState({isloading:true})
    fetch(URL+"/APP-API/App/UpdateSingleAddress",{
      method:'post',
      header:{
        'Accept': 'application/json',
        'Content-type': 'application/json'
      },
      body:JSON.stringify({

        AddressId:this.state.addressId,
        UserFullName:UserFullName,
        UserAddress:UserAddress,
        UserCity:UserCity,
        UserZip:UserZip,
        UserPhone:UserPhone,
        UserAlternativePhone:UserAlternativePhone,
      })


  }) .then((response) => response.json())
  .then((responseJson) => {

    this.setState({isloading:false})




  var msg = 'Your address updated successfully'
    toast.success(msg)



    this.handleNext("/"+this.props.match.params.screen)




  })
  .catch((error) => {
    //  console.error(error);

  });

    }




  }



}

